INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,3,'뒤집어 세운 마룻바닥을 방패로 하여 자신과
같은 편으로 향하는 기술 데미지를 막는다.
