INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,5,'Retourne un tatami pour bloquer, comme avec un
bouclier, les capacités visant le lanceur ou ses
alliés. N’a pas d’effet sur les attaques de statut.');
INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,6,'Der Anwender richtet eine Tatami-Matte auf,
um sich und sein Team vor Schaden zu schützen.
Kein Schutz vor Status-Attacken.');
INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,7,'El usuario usa un tatami para escudarse de los
movimientos enemigos. Protege también a los
aliados. No funciona contra ataques de estado.');
INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,8,'Chi la usa protegge se stesso e i propri alleati dai
danni di mosse nemiche, adoperando un tappetino
come scudo. Non è efficace contro mosse di stato.');
INSERT INTO [move_flavor_text] (move_id,version_group_id,language_id,flavor_text) VALUES (561,15,9,'Using a pulled-up mat as a shield, the user
protects itself and its allies from damaging moves.
